import "./app.css";
import React, { useEffect, useState } from "react";
import Map, { Marker, Popup, NavigationControl } from "react-map-gl";
import { Room, Star } from '@mui/icons-material';
import 'mapbox-gl/dist/mapbox-gl.css';
import axios from "axios";
import { format } from "timeago.js";
import Register from "./components/Register";
import Login from "./components/Login";



function App() {
  const myStorage = window.localStorage;
  const [showPopup, setShowPopup] = React.useState(true);
  const [currentPlaceId, setCurrentPlaceId] = useState(null);
  const [pins, setPins] = useState([]);
  const [newPlace, setNewPlace] = useState(null);
  const [title, setTitle] = useState(null);
  const [desc, setDesc] = useState(null);
  const [star, setStar] = useState(0);
  const [currentUser, setCurrentUser] = useState(myStorage.getItem("user"));

  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [viewport, setViewport] = useState({
    latitude: 52.360001,
    longitude: 4.885278,
    zoom: 8,
  });

  const handleMarkerClick = (id, lat, long) => {
    setCurrentPlaceId(id);
    setViewport({ ...viewport, latitude: lat, longitude: long });
  };

  const handleAddClick = (e) => {
    // const { lat, lng: long } = e.lngLat;
    // setNewPlace({
    //   lat,
    //   long,
    // });
    console.log(e);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newPin = {
      username: currentUsername,
      title,
      desc,
      rating: star,
      lat: newPlace.lat,
      long: newPlace.long,
    };

    try {
      const res = await axios.post("/pins", newPin);
      setPins([...pins, res.data]);
      setNewPlace(null);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    const getPins = async () => {
      try {
        const allPins = await axios.get("/pins");
        console.log(allPins);
        setPins(allPins.data);
      } catch (err) {
        console.log(err);
      }
    };
    getPins();
  }, []);

   const handleLogout = () => {
    setCurrentUsername(null);
    myStorage.removeItem("user");
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
        <Map
          // {...viewport}
           initialViewState={{
            longitude: 4.885278,
            latitude: 52.360001,
            zoom: 8
           }}
           mapboxAccessToken={process.env.REACT_APP_MAPBOX}
           width="100%"
           height="100%"
           transitionDuration="200"
           mapStyle="mapbox://styles/genie1234/cl1fbk116000s14qu6d2rgu0s"
           onDblClick={currentUser && handleAddClick}
           onDblClick={currentUser && handleAddClick}
        >
       {pins.map((p) => (
           <>
            <Marker
              longitude={p.long}
              latitude={p.lat}
              anchor="bottom"
            >
            {p.username === currentUser? (
              <div className="room-container-current">
              <Room
               onClick={() => handleMarkerClick(p._id, p.lat, p.long)}
              />
              </div>
             ):(
              <div className="room-container">
              <Room
               onClick={() => handleMarkerClick(p._id, p.lat, p.long)}
              />
              </div>
             )
             }
            </Marker>
            {p._id === currentPlaceId && (
             <Popup
              latitude={p.lat}
              longitude={p.long}
              closeButton={true}
              closeOnClick={false}
              onClose={() => setCurrentPlaceId(null)}
              anchor="left"
             >
             <div class="card">
               <label>Place</label>
               <h4 className="place">{p.title}</h4>
               <label>Review</label>
               <h4 className="place">{p.desc}</h4>
               <label>Rating</label>
               <div className="star">
                 <Star />
                 <Star />
                 <Star />
                 <Star />
                 <Star />
               </div>
               <label>Information</label>
               <span className="username">Created by <b>{p.username}</b></span>
               <span className="date">{format(p.createdAt)}</span>
             </div>
             </Popup>
            )}
           </>
          ))}

          {newPlace && (
           <>
             {/* <Marker
              latitude={newPlace.lat}
              longitude={newPlace.long}
              offsetLeft={-3.5 * viewport.zoom}
              offsetTop={-7 * viewport.zoom}
             >
               <Room />
             </Marker> */}
             {/* <Popup
              latitude={newPlace.lat}
              longitude={newPlace.long}
              closeButton={true}
              closeOnClick={false}
              onClose={() => setNewPlace(null)}
              anchor="left"
             >
             Hello */}
              {/* <div>
                <form onSubmit={handleSubmit}>
                  <label>Title</label>
                  <input
                    placeholder="Enter a title"
                    autoFocus
                    onChange={(e) => setTitle(e.target.value)}
                  />
                  <label>Description</label>
                  <textarea
                    placeholder="Say us something about this place."
                    onChange={(e) => setDesc(e.target.value)}
                  />
                  <label>Rating</label>
                  <select onChange={(e) => setStar(e.target.value)}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                  </select>
                  <button type="submit" className="submitButton">
                    Add Pin
                  </button>
                </form>
              </div> */}
            {/* </Popup> */}
           </>
          )}

          {/* {currentUser ? (
            <button className="button logout">
               Log out
             </button> ) : (
             <div className="buttons">
              <button className="button login" onClick={() => setShowLogin(true)}>
               Log in
              </button>
              <button  className="button register" onClick={() => setShowRegister(true)}>
                Register
              </button>
             </div>
          )}
           {showRegister && <Register setShowRegister={setShowRegister} />}
            {showLogin && (
            <Login
              setShowLogin={setShowLogin}
            />
            )} */}
           {/* {currentUsername ? (
             <button className="button logout" onClick={handleLogout}>
               Log out
             </button>
             ) : (
             <div className="buttons">
             <button className="button login" onClick={() => setShowLogin(true)}>
              Log in
             </button>
             <button  className="button register" onClick={() => setShowRegister(true)}>
               Register
             </button>
            </div>
            )}

            <Register />
            {showRegister && <Register setShowRegister={setShowRegister} />}
            {showLogin && (
            <Login
              setShowLogin={setShowLogin}
              setCurrentUsername={setCurrentUsername}
              myStorage={myStorage}
            />
        )} */}
        </Map>
    </div>
  );
}

export default App;
